mapper.insert:

INSERT INTO board (
    board_title, board_passwd, board_content, board_regdate
)
VALUES (
    #{board_title}, password(#{board_passwd}), #{board_content},now()
)

mapper.select:
SELECT b.board_num, b.user_number, b.board_content, b.board_regdate , u.id, u.name FROM board b inner join user u on b.user_number = u.user_number WHERE 1=1
AND b.board_content like concat('%', ${searchKeyword}, '%')
ORDER BY b.board_num DESC LIMIT #{limitBegin}, #{articleCount}
